import React, { useState } from "react";
import './mainContent.css';
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faCaretDown } from "@fortawesome/free-solid-svg-icons"; 

const MainContent = () => {
  const [selectedOption, setSelectedOption] = useState("USD"); 
  const [showDropdown, setShowDropdown] = useState(false);

  const handleOptionSelect = (option) => {
    setSelectedOption(option);  
    setShowDropdown(false);     
  };

  return (
    <div className="Main">
      <span className="amtEnterBox">
        <span className="amtEnterText">Enter Your Amount:</span>
        <input className="amtEnterInput" />
      </span>
      <span className="convertBox">
        <span className="convertText">Convert into</span>
        <div className="dropdownContainer">
          <button className="dropdownBtn" onClick={() => setShowDropdown(!showDropdown)}>
            {selectedOption} <FontAwesomeIcon icon={faCaretDown} />
          </button>
          {showDropdown && (
            <ul className="dropdownMenu">
              <li onClick={() => handleOptionSelect("USD")}>USD</li>
              <li onClick={() => handleOptionSelect("Euro")}>Euro</li>
              <li onClick={() => handleOptionSelect("Bitcoin")}>Bitcoin</li>
              <li onClick={() => handleOptionSelect("Ethereum")}>Ethereum</li>
            </ul>
          )}
        </div>
        <input className="convertDropDown" />
      </span>
      <button className="proceedBtn">Proceed for transaction</button>
    </div>
  );
};

export default MainContent;
