import React from "react";
import './wallet.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faCopy } from '@fortawesome/free-regular-svg-icons'; 
import {selectedOption} from '../mainContent/mainContent'

const Wallet = () => {
    return (
        <div className="wallet">
            <text className="heading">Wallet Address:</text> 
            <br></br>
            <div className="walletBox">
                <div className="walletBoxHeader">
                    <text className="subHeadingLeft"> Address</text>
                    <a href="#"><FontAwesomeIcon className="subHeadingRight" icon={faCopy} /></a>
                </div>
                <span className="walletAddressText">Here is your wallet address.</span> 
            </div>
        </div>
    );
};

export default Wallet;
