import React, { useState } from "react";
import './mainContent.css';
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";

const MainContent = () => {
  const [selectedOption, setSelectedOption] = useState(`USD ${<FontAwesomeIcon icon="fa-solid fa-caret-down" />}`); 
  const [showDropdown, setShowDropdown] = useState(false); 

  const handleOptionSelect = (option) => {
    setSelectedOption(option);  
    setShowDropdown(false);     
  };

  return (
    <div className="Main">
      <span className="amtEnterBox">
        <text className="amtEnterText">Enter Your Amount:</text>
        <input className="amtEnterInput" />
      </span>
      <span className="convertBox">
        <text className="convertText">Convert into</text>
        <button className="dropdownBtn" onClick={() => setShowDropdown(!showDropdown)}>
          {selectedOption}
        </button>
        {showDropdown && (
          <ul className="dropdownMenu">
            <li onClick={() => handleOptionSelect(`USD ${<FontAwesomeIcon icon="fa-solid fa-caret-down" />} `)}>USD</li>
            <li onClick={() => handleOptionSelect(`Euro ${<FontAwesomeIcon icon="fa-solid fa-caret-down" />}`)}>Euro</li>
            <li onClick={() => handleOptionSelect(`Bitcoin ${<FontAwesomeIcon icon="fa-solid fa-caret-down" />}`)}>Bitcoin</li>
            <li onClick={() => handleOptionSelect(`Ethereum ${<FontAwesomeIcon icon="fa-solid fa-caret-down" />}`)}>Ethereum</li>
          </ul>
        )}
        <input className="convertDropDown" />
      </span>
    </div>
  );
};

export default MainContent;
