import React, { useState } from "react";
import './mainContent.css';

const MainContent = () => {
  const [selectedOption, setSelectedOption] = useState('Select Currency'); // State for the selected option
  const [showDropdown, setShowDropdown] = useState(false); // State to toggle dropdown visibility

  const handleOptionSelect = (option) => {
    setSelectedOption(option);  // Update the button label with the selected option
    setShowDropdown(false);     // Close the dropdown after selection
  };

  return (
    <div className="Main">
      <span className="amtEnterBox">
        <text className="amtEnterText">Enter Your Amount:</text>
        <input className="amtEnterInput" />
      </span>
      <span className="convertBox">
        <text className="convertText">Convert into</text>

        {/* Button that shows the dropdown when clicked */}
        <button className="dropdownBtn" onClick={() => setShowDropdown(!showDropdown)}>
          {selectedOption}
        </button>

        {/* Dropdown menu that appears when showDropdown is true */}
        {showDropdown && (
          <ul className="dropdownMenu">
            <li onClick={() => handleOptionSelect('USD')}>USD</li>
            <li onClick={() => handleOptionSelect('Euro')}>Euro</li>
            <li onClick={() => handleOptionSelect('Bitcoin')}>Bitcoin</li>
            <li onClick={() => handleOptionSelect('Ethereum')}>Ethereum</li>
          </ul>
        )}

        <text className="convertDisplayText"></text>
      </span>
    </div>
  );
};

export default MainContent;
