import React from "react";
import './wallet.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faCopy } from '@fortawesome/free-regular-svg-icons'; 

const Wallet = () => {
    return (
        <div className="wallet">
            <text className="heading">Wallet Address:</text> 
            <br></br>
            <span className="walletBox">
                <span className="walletBoxHeader">
                    <span className="subHeadingLeft">Bitcoin wallet Address</span>
                    <span className="subHeadingRight">
                        <FontAwesomeIcon icon={faCopy} /> Copy code 
                    </span>
                </span>
                <span className="walletAddressText">Here is your wallet address.</span> 
            </span>
            <button className="proceedBtn">Proceed</button>
        </div>
    );
};

export default Wallet;
