import React from "react";
import './wallet.css'

const Wallet=()=>{
    return(
        <div className="wallet">
            <text className="heading">Wallet Address:</text>
            <span className="walletBox">
                <span className="walletBoxHeader">
                    <span className="subHeadingLeft">Bitcoin wallet Address</span>
                    <span className="subHeadingRight"><FontAwesomeIcon icon="fa-regular fa-copy" /> Copy code</span>
                </span>
                <text className="walletAddressText">Here is your wallet address.</text>
            </span>
            <button className="proceedBtn">Proceed</button>
        </div>
    )
}

export default Wallet;
